mapper.select:
SELECT tc.dept_name, tc.name, tc.phone, tc.user_name, td.age, ti.create_time, ti.create_user_name, tk.name as apply_key, tc.source_name, tc.company_name, td.major, td.position_applied, (CASE td.education_id WHEN 0 THEN '无' WHEN 1 THEN '小学' WHEN 2 THEN '初中'WHEN 3 THEN '高中'WHEN 4 THEN '大专'WHEN 5 THEN '本科'WHEN 6 THEN '硕士'WHEN 7 THEN '博士'ELSE '/'END) AS education_id, (CASE td.work_experience WHEN -1 THEN '应届生' WHEN 0 THEN '无经验' WHEN 1 THEN '一年' WHEN 2 THEN '两年' WHEN 3 THEN '三年' WHEN td.work_experience >3 THEN '三年以上'ELSE '/' END) AS work_experience, (CASE tc.status WHEN 0 THEN '潜在' WHEN 1 THEN '失败有效' WHEN 2 THEN '失败无效' WHEN 3 THEN '预约' WHEN 4 THEN '成交' END) AS status, (CASE tc.get_way WHEN 1 THEN '主动' WHEN 2 THEN '被动' END) AS get_way FROM transfer_customer tc INNER JOIN transfer_customer_detail td ON tc.customer_id = td.customer_id INNER JOIN transfer_customer_invalid ti ON ti.customer_id = tc.customer_id LEFT JOIN transfer_keyword tk ON tk.id=td.apply_key
and tc.dept_id in ${userAllDeptId}
and tc.user_id=#{userId}
and tc.user_name=#{userName}
and tc.name like concat('%',#{name},'%') and tc.phone like concat('%',#{phone},'%')
and tc.create_time between #{beginCreateTime} and #{endCreateTime}
and ti.create_time between #{beginInvalidTime} and #{endInvalidTime}
GROUP BY tc.customer_id
