mapper.select:
SELECT COUNT(customer_id) AS num ,dept_id AS deptId
FROM transfer_customer
WHERE create_time BETWEEN #{beginTime} AND #{endTime}

    AND pro_id=#{proId}


    AND source_id=#{sourceId}


    AND company_id=#{companyId}


    AND get_way=#{getWay}

GROUP BY deptId

SELECT COUNT(customer_id) AS num ,dept_id AS deptId
FROM transfer_customer
WHERE first_user_id IS NOT NULL
AND create_time BETWEEN #{beginTime} AND #{endTime}

    AND pro_id=#{proId}


    AND source_id=#{sourceId}


    AND company_id=#{companyId}


    AND get_way=#{getWay}

GROUP BY deptId

SELECT COUNT(customer_id) AS num ,dept_id AS deptId
FROM transfer_customer
WHERE `status` IN(0,1,3,4)
AND create_time BETWEEN #{beginTime} AND #{endTime}

    AND pro_id=#{proId}


    AND source_id=#{sourceId}


    AND company_id=#{companyId}


    AND get_way=#{getWay}

GROUP BY deptId

SELECT COUNT(customer_id) AS num ,dept_id AS deptId
FROM transfer_customer
WHERE `status` IN(0,3,4)
AND create_time BETWEEN #{beginTime} AND #{endTime}

    AND pro_id=#{proId}


    AND source_id=#{sourceId}


    AND company_id=#{companyId}


    AND get_way=#{getWay}

GROUP BY deptId

SELECT COUNT(v.customer_id) AS num ,v.dept_id AS deptId
FROM(
SELECT r.customer_id ,r.dept_id
FROM transfer_customer_reservation r
INNER JOIN transfer_customer c ON r.customer_id=c.customer_id
WHERE r.visit_status=1
AND r.visit_time BETWEEN #{beginTime} AND #{endTime}

    AND c.pro_id=#{proId}


    AND c.source_id=#{sourceId}


    AND c.company_id=#{companyId}


    AND c.get_way=#{getWay}

GROUP BY r.customer_id
) v
GROUP BY deptId;

SELECT COUNT(v.customer_id) AS num ,v.dept_id AS deptId
FROM(
SELECT r.customer_id ,r.dept_id
FROM transfer_customer_reservation r
INNER JOIN transfer_customer c ON r.customer_id=c.customer_id
WHERE r.valid_visit=1
AND r.visit_time BETWEEN #{beginTime} AND #{endTime}

    AND c.pro_id=#{proId}


    AND c.source_id=#{sourceId}


    AND c.company_id=#{companyId}


    AND c.get_way=#{getWay}

GROUP BY r.customer_id
) v
GROUP BY deptId;

SELECT COUNT(v.customer_id) AS num ,v.dept_id AS deptId
FROM(
SELECT r.customer_id ,r.dept_id
FROM transfer_customer_reservation r
INNER JOIN transfer_customer c ON r.customer_id=c.customer_id
WHERE r.intention=1
AND r.visit_time BETWEEN #{beginTime} AND #{endTime}

    AND c.pro_id=#{proId}


    AND c.source_id=#{sourceId}


    AND c.company_id=#{companyId}


    AND c.get_way=#{getWay}

GROUP BY r.customer_id
) v
GROUP BY deptId;

SELECT COUNT(d.customer_id) AS num ,d.dept_id AS deptId
FROM transfer_customer_deal d
INNER JOIN transfer_customer c ON c.customer_id=d.customer_id
WHERE d.is_deleted=0
AND d.create_time BETWEEN #{beginTime} AND #{endTime}

    AND c.pro_id=#{proId}


    AND c.source_id=#{sourceId}


    AND c.company_id=#{companyId}


    AND c.get_way=#{getWay}

GROUP BY deptId;
