mapper.select:
SELECT COUNT(v.customer_id) AS num ,v.dept_id AS deptId
FROM(
SELECT r.customer_id ,r.dept_id
FROM transfer_customer_reservation r
INNER JOIN transfer_customer c ON r.customer_id=c.customer_id
WHERE r.visit_status=1
AND r.visit_time BETWEEN #{beginTime} AND #{endTime} AND c.dept_id IN ${deptIds}

    AND c.get_way=#{getWay}

GROUP BY r.customer_id
) v
GROUP BY deptId;

SELECT COUNT(customer_id) AS num ,dept_id AS deptId
FROM transfer_customer
WHERE `status` IN(0,1,3,4)
AND create_time BETWEEN #{beginTime} AND #{endTime} AND dept_id IN ${deptIds}

    AND get_way=#{getWay}

GROUP BY userId, deptId

SELECT COUNT(customer_id) AS num , user_id AS userId, dept_id AS deptId
FROM transfer_customer
WHERE create_time BETWEEN #{beginTime} AND #{endTime} AND dept_id IN ${deptIds}

    AND get_way=#{getWay}

GROUP BY userId, deptId

SELECT COUNT(d.customer_id) AS num ,d.dept_id AS deptId FROM transfer_customer_deal d INNER JOIN transfer_customer c ON c.customer_id=d.customer_id WHERE d.is_deleted=0 AND d.create_time BETWEEN #{beginTime} AND #{endTime} AND c.dept_id IN ${deptIds}
AND c.get_way=#{getWay}
GROUP BY deptId

SELECT COUNT(v.customer_id) AS num ,v.dept_id AS deptId
FROM(
SELECT r.customer_id ,r.dept_id
FROM transfer_customer_reservation r
INNER JOIN transfer_customer c ON r.customer_id=c.customer_id
WHERE r.visit_status=1 and c.status=3 and to_days(r.appointment_time) = to_days(now())
AND r.visit_time BETWEEN #{beginTime} AND #{endTime} AND c.dept_id IN ${deptIds}

    AND c.get_way=#{getWay}

GROUP BY r.customer_id
) v
GROUP BY deptId;

SELECT COUNT(v.customer_id) AS num ,v.dept_id AS deptId
FROM(
SELECT r.customer_id ,r.dept_id
FROM transfer_customer_reservation r
INNER JOIN transfer_customer c ON r.customer_id=c.customer_id
WHERE r.visit_status=1 and c.status=3 and to_days(r.appointment_time) - to_days( now( ) ) = 1
AND r.visit_time BETWEEN #{beginTime} AND #{endTime} AND c.dept_id IN ${deptIds}

    AND c.get_way=#{getWay}

GROUP BY r.customer_id
) v
GROUP BY deptId;

SELECT COUNT(v.customer_id) AS num ,v.dept_id AS deptId FROM( SELECT r.customer_id ,r.dept_id FROM transfer_customer_reservation r INNER JOIN transfer_customer c ON r.customer_id=c.customer_id WHERE r.valid_visit=1 AND r.visit_time BETWEEN #{beginTime} AND #{endTime} AND c.dept_id IN ${deptIds}
AND c.get_way=#{getWay}
GROUP BY r.customer_id ) v GROUP BY deptId
