mapper.select:
SELECT COUNT(DISTINCT tcr.customer_id) AS num, tc.create_user_dept_id AS deptId FROM transfer_call_record tcr inner join transfer_customer tc on tc.customer_id = tcr.customer_id WHERE tcr.status = false AND tcr.create_time BETWEEN #{beginTime} AND #{endTime} AND tc.dept_id IN ${deptIds}
AND tcr.create_user_id=#{userId}
GROUP BY tcr.create_user_id

SELECT COUNT(DISTINCT tcr.customer_id) AS num, tc.create_user_dept_id AS deptId
FROM transfer_call_record tcr
inner join transfer_customer tc on tc.customer_id = tcr.customer_id
WHERE tcr.create_time BETWEEN #{beginTime} AND #{endTime} AND tc.dept_id IN ${deptIds}

	AND tcr.create_user_id=#{userId}

GROUP BY tcr.create_user_id

		SELECT SUM(TIME_TO_SEC(tcr.total_duration)) AS duration, tc.create_user_dept_id AS deptId
		FROM transfer_call_record tcr
		inner join transfer_customer tc on tc.customer_id = tcr.customer_id
        WHERE tcr.create_time BETWEEN #{beginTime} AND #{endTime} AND tc.dept_id IN ${deptIds}

			AND tcr.create_user_id=#{userId}

        GROUP BY tcr.create_user_id
